insert into order_status (code, name, description) values ('PDG', 'Pending', 'Customer started the checkout process but did not complete it. Incomplete orders are assigned a "Pending" status and can be found under the More tab in the View Orders screen.');
insert into order_status (code, name, description) values ('APT', 'Awaiting Payment', 'Customer has completed the checkout process, but payment has yet to be confirmed. Authorize only transactions that are not yet captured have this status.');
insert into order_status (code, name, description) values ('AFF', 'Awaiting Fulfillment', 'Customer has completed the checkout process and payment has been confirmed.');
insert into order_status (code, name, description) values ('ATS', 'Awaiting Shipment', 'Order has been pulled and packaged and is awaiting collection from a shipping provider.');
insert into order_status (code, name, description) values ('ATP', 'Awaiting Pickup', 'Order has been packaged and is awaiting customer pickup from a seller-specified location.');
insert into order_status (code, name, description) values ('PLS', 'Partially Shipped', ' Only some items in the order have been shipped, due to some products being pre-order only or other reasons.');
insert into order_status (code, name, description) values ('COM', 'Completed', 'Order has been shipped/picked up, and receipt is confirmed; client has paid for their digital product, and their file(s) are available for download.');
insert into order_status (code, name, description) values ('SHP', 'Shipped', 'Order has been shipped, but receipt has not been confirmed; seller has used the Ship Items action. A listing of all orders with a "Shipped" status can be found under the More tab of the View Orders screen.');
insert into order_status (code, name, description) values ('CND', 'Cancelled', 'Seller has cancelled an order, due to a stock inconsistency or other reasons. Stock levels will automatically update depending on your Inventory Settings. Cancelling an order will not refund the order. This status is triggered automatically when an order using an authorize-only payment gateway is voided in the control panel before capturing payment.');
insert into order_status (code, name, description) values ('DEC', 'Declined', 'Seller has marked the order as declined for lack of manual payment, or other reasons');
insert into order_status (code, name, description) values ('REF', 'Refunded', 'Seller has used the Refund action. A listing of all orders with a "Refunded" status can be found under the More tab of the View Orders screen.');
insert into order_status (code, name, description) values ('DSP', 'Disputed', 'Customer has initiated a dispute resolution process for the PayPal transaction that paid for the order.');
insert into order_status (code, name, description) values ('MVR', 'Manual Verification Required', 'Order on hold while some aspect (e.g. tax-exempt documentation) needs to be manually confirmed. Orders with this status must be updated manually. Capturing funds or other order actions will not automatically update the status of an order marked Manual Verification Required.');
insert into order_status (code, name, description) values ('PRE', 'Partially Refunded', 'Seller has partially refunded the order.');
